import { defineBlock } from "cmssy-cli/config";

export default defineBlock({
  name: "My Block",
  description: "A custom content block",
  category: "marketing",

  schema: {
    heading: {
      type: "singleLine",
      label: "Heading",
      required: true,
    },
    description: {
      type: "multiLine",
      label: "Description",
    },
    showButton: {
      type: "boolean",
      label: "Show CTA Button",
      defaultValue: true,
    },
  },
});

export default function MyBlock({ content }) {
  const { heading, description, showButton = true } = content;

  return (
    <section className="py-24">
      <div className="max-w-4xl mx-auto px-4 text-center">
        <h2 className="text-4xl font-bold mb-4">{heading}</h2>
        {description && (
          <p className="text-lg text-muted-foreground mb-8">
            {description}
          </p>
        )}
        {showButton && (
          <button className="bg-primary text-primary-foreground px-6 py-3 rounded-lg">
            Get Started
          </button>
        )}
      </div>
    </section>
  );
}

showButton: {
  type: "boolean",
  label: "Show CTA Button",
  defaultValue: true,
},
buttonText: {
  type: "singleLine",
  label: "Button Text",
  defaultValue: "Get Started",
  showWhen: { field: "showButton", equals: true },
},

{
  "layoutPositions": {
    "header": {
      "type": "@cmssy-marketing/blocks.header",
      "content": {
        "logoText": "cmssy",
        "navigation": [
          { "label": "Features", "url": "/features" },
          { "label": "Pricing", "url": "/pricing" }
        ]
      }
    },
    "footer": {
      "type": "@cmssy-marketing/blocks.footer",
      "content": { "copyrightText": "cmssy. All rights reserved." }
    }
  },
  "pages": [
    {
      "name": "Homepage",
      "slug": "/",
      "blocks": [
        {
          "type": "@cmssy-marketing/blocks.hero",
          "content": { "heading": "Build reusable UI blocks" }
